import React from "react";
import { useState, useEffect } from "react";
import { Row, CardGroup } from "react-bootstrap";
import axios from "axios";
import ColorSchemesExample from "../NavBar/navbar";
import "./series.scss";
import MovieShowDetails from "../ModalMovie/modalmovie";
import MovieCard from "../Movie/movie";
import CarouselMovies from "../Carousel/carousel";
import { Pagination } from "react-bootstrap";
import "./series.scss"
// this is the favorite page cards listing 
export default function Series(req, res) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [Series, setSeries] = useState([]);
  const [OneSeries, setOneSeries] = useState([]);
  const [page, setPage] = useState(1);
  const fetchData = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_HOST}/series/${page}`);
      setSeries(res.data.Trending);
      console.log(res.data.Trending);
    } catch (err) {
      console.log(err);
    }
  };
  const changePage = (newPage) => {
      setPage(newPage);
  };
useEffect(() => {
  fetchData();
}, [page]);
  return (
    <div>
      <ColorSchemesExample />

      <CarouselMovies />
      <h1 className="sign">Most Popular</h1>
      <div className="positions">


        <CardGroup>
          {Series.length &&
            Series.map((series) => (
              <MovieCard
                handleShow={handleShow}
                movieData={series}
                setMovie={setOneSeries}
              />
            ))}
        </CardGroup>

      </div>
      {
        setOneSeries &&
        <MovieShowDetails
          show={show}
          handleClose={handleClose}
          Movie={OneSeries}

        />
      }
      <footer>
      <nav aria-label="Page navigation example">
          <ul className="pagination">
            {page === 1 ? (
              <li className="page-item disabled">
                <a className="page-link">Previous</a>
              </li>
            ) : (
              <li className="page-item">
                <a className="page-link" onClick={() => changePage(page - 1)}>Previous</a>
              </li>
            )}
            {[...Array(totalPages)].map((_, index) => (
              <li key={index + 1} className={`page-item ${page === index + 1 ? 'active' : ''}`}>
                <a className="page-link" onClick={() => changePage(index + 1)}>{index + 1}</a>
              </li>
            ))}
            {page === totalPages ? (
              <li className="page-item disabled">
                <a className="page-link">Next</a>
              </li>
            ) : (
              <li className="page-item">
                <a className="page-link" onClick={() => changePage(page + 1)}>Next</a>
              </li>
            )}
          </ul>
        </nav>
      </footer>
    </div>

  )
}
