import React from "react";
import { useState, useEffect } from "react";
import { Row, CardGroup } from "react-bootstrap";
import axios from "axios";
import ColorSchemesExample from "../NavBar/navbar";
import "./series.scss";
import MovieShowDetails from "../ModalMovie/modalmovie";
import MovieCard from "../Movie/movie";
import CarouselMovies from "../Carousel/carousel";
import { Pagination } from "react-bootstrap";
import "./series.scss"
// this is the favorite page cards listing 
export default function Series(req,res) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [Series, setSeries] = useState([]);
  const [OneSeries, setOneSeries] = useState([]);
  const [page,setPage]=useState(5);
  const fetchData = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_HOST}/series/${page}`);
      setSeries(res.data.Trending);
      console.log(res.data.Trending);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    fetchData()
  }, []);
  return (
    <div>
    <ColorSchemesExample />

    <CarouselMovies/>
<h1 className="sign">Most Popular</h1>
<div className="positions">


    <CardGroup>
      {Series.length &&
        Series.map((series) => (
          <MovieCard
            handleShow={handleShow}
            movieData={series}
            setMovie={setOneSeries}
          />
        ))}
    </CardGroup>

</div>
    {
    setOneSeries &&
      <MovieShowDetails
        show={show}
        handleClose={handleClose}
        Movie={OneSeries}

      />
    }
      <footer>
      <nav aria-label="Page navigation example">
  <ul class="pagination">
{

page==1&&  <li class="page-item disabled"><a class="page-link" >Previous</a></li>
|| <li class="page-item "><a class="page-link" >Previous</a></li>
}
    <li class="page-item"><a class="page-link" href="#">{page}</a></li>
    <li class="page-item"><a class="page-link" href="#">{page+1}</a></li>
    <li class="page-item"><a class="page-link" href="#">{page+2}</a></li>
    {
!OneSeries.length&&
    <li class="page-item"><a class="page-link" href="#">Next</a></li>
    }
  </ul>
</nav>
</footer>
  </div>

  )
}
