import './carousal.scss'
import React from 'react';
import {
  MDBCarousel,
  MDBCarouselItem,
} from 'mdb-react-ui-kit';

export default function CarouselMovies({movie}) {
  return (
    <div className='carsoulControl'>




    <MDBCarousel showControls showIndicators fade>
      <MDBCarouselItem
        className='w-100 d-block'
        itemId={1}
        src={`https://image.tmdb.org/t/p/w500/${movie[0].Poster_Path}`}
        alt='...'

      >

      </MDBCarouselItem>
      <MDBCarouselItem
        className='w-100 d-block'
        itemId={2}
        src={`https://image.tmdb.org/t/p/w500/${movie[1].Poster_Path}`}
        alt='...'

      >

      </MDBCarouselItem>
      <MDBCarouselItem
        className='w-100 d-block'
        itemId={3}
        src={`https://image.tmdb.org/t/p/w500/${movie[2].Poster_Path}`}
        alt='...'
      >


      </MDBCarouselItem>
      <MDBCarouselItem
        className='w-100 d-block'
        itemId={4}
        src='https://image.tmdb.org/t/p/w500/${movie[3].Poster_Path} '
        alt='...'
      >

      </MDBCarouselItem>
    </MDBCarousel>
    </div>
  );
}
