import './carousal.scss';
import React from 'react';
import {
  MDBCarousel,
  MDBCarouselItem,
} from 'mdb-react-ui-kit';

export default function CarouselSeries({ series }) {
  console.log(series);

  // Ensure series is defined and has at least 3 elements
  if (!series || series.length < 3) {
    return <div>Loading...</div>; // You can replace this with a loading spinner or any placeholder
  }

  return (
    <div className='carouselControl'>
      <MDBCarousel showControls showIndicators fade>
        {
          series.slice(1, 3).map((serie, i) => (
            <MDBCarouselItem
              key={i}
              className='w-100 d-block'
              itemId={i}
              src={`https://image.tmdb.org/t/p/w500/${serie.Poster_Path}`}
              alt={serie.title || 'Image'} // Use the title of the series or a default alt text
            >
            </MDBCarouselItem>
          ))
        }
      </MDBCarousel>
    </div>
  );
}
