import React from "react";
import MovieShowFavDetails from "../ModalMovie/modalFavMovie";
import { useState, useEffect } from "react";
import MovieFavCard from "../Movie/movieFav";
import { Row, CardGroup } from "react-bootstrap";
import axios from "axios";
import ColorSchemesExample from "../NavBar/navbar";
import "./series.scss";
import MovieShowDetails from "../ModalMovie/modalmovie";
import MovieCard from "../Movie/movie";
// this is the favorite page cards listing 
export default function Series() {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [Series, setSeries] = useState([]);
  const [OneSeries, setOneSeries] = useState([]);
  const [count, setCounter] = useState(0)
  const fetchData = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_HOST}/series`);
      setSeries(res.data.Trending);
      console.log(res.data.Trending);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    fetchData()
  }, []);
  return (
    <>

      <ColorSchemesExample />
      <CardGroup>
        {Series.length && Series.map(series => {
          <MovieCard
            handleShow={handleShow}
            movieData={series}
            setMovie={setOneSeries}
          />
        })}
      </CardGroup>
    {
      setOneSeries.length&&Movie
    }
    </>
  )
}
